import{s as l}from"./auth-aIv_B0Df.js";/* empty css                 */const o=document.getElementById("login-form"),n=document.getElementById("login-msg");document.querySelectorAll(".pw-toggle").forEach(t=>{t.addEventListener("click",()=>{const e=t.parentElement.querySelector("input"),s=t.querySelector("i");e.type==="password"?(e.type="text",s.classList.replace("fa-eye","fa-eye-slash")):(e.type="password",s.classList.replace("fa-eye-slash","fa-eye"))})});o.addEventListener("submit",async t=>{t.preventDefault(),n.textContent="";const e=o.email.value.trim(),s=o.password.value;if(!e||!s){n.textContent="Enter email and password.";return}try{await l(e,s),n.style.color="#2a8f3a",n.textContent="Login successful. Redirecting...",setTimeout(()=>location.href="index.html",800)}catch(a){n.textContent=a.message||"Login failed."}});
